import { Extension } from '../../../extension/index';
import { Config } from '../../config';
import { latest } from '../../../../src';
export interface BaseRequest {
    app: typeof Config.config.title;
    type: Extension.TYPES.REQUEST;
    id: string;
}
export interface BaseResponse {
    app: typeof Config.config.title;
    type: Extension.TYPES.RESPONSE;
    id: string;
}
export interface BaseFullResponse {
    createdAt: number;
    resolvedAt?: number;
}
export interface PaymentDetails {
    destination: string;
    amount: string;
    tag?: string;
    memo?: string;
}
export interface SignOpts {
    isModifiable?: boolean;
    description?: string;
}
